DELIMITER $$

CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_vehicles` (IN `_operacion` INT)
BEGIN
    IF _operacion = 1 THEN
        SELECT 
            v.id, vi.image, v.name, v.plate, 
            b.name AS brand_name, 
            m.name AS model_name, 
            vt.name AS type_name, 
            v.year
        FROM vehicles v 
        INNER JOIN brands b ON v.brand_id = b.id
        INNER JOIN brandmodels m ON v.model_id = m.id
        INNER JOIN vehicletypes vt ON v.type_id = vt.id
        LEFT JOIN vehicleimages vi ON vi.vehicle_id = v.id AND vi.profile = 1;
    END IF;
END$$

